function cto_loading_show(){
		//show loading
		document.getElementById("cto_overlay").style.display = "block";
	}
	
	function cto_loading_hide(){
		//hide loading
		document.getElementById("cto_overlay").style.display = "none";
	}
	
	function cto_messages_show(ctoajx_response){
		var d = document.getElementById("cto_messages");
		if(ctoajx_response["status"]){
			d.className += "alert alert-success alert-dismissible";
			d.innerHTML = ctoajx_response["messages"];
		}else{
			d.className += "alert alert-danger alert-dismissible";
			d.innerHTML = ctoajx_response["messages"];
		}
		
		document.getElementById("cto_messages").style.display = "block";
	}
	
	function cto_messages_hide(){
		var d = document.getElementById("cto_messages");
		d.className = d.className.replace(/\balert-danger\b/g, "");
		d.className = d.className.replace(/\balert-dismissible\b/g, "");
		d.className = d.className.replace(/\balert\b/g, "");
		d.innerHTML = "";
		document.getElementById("cto_messages").style.display = "block";
	}
	
	function ctoerr_messages(ctojs_fiel, ctoajx_response){
		for(cto_i = 0; cto_i < ctojs_fiel.length; cto_i++){
			if(ctoajx_response['err_'+ctojs_fiel[cto_i]]){
				//add class for class form-group with class has-error
				var d = document.getElementById("ctof_"+ctojs_fiel[cto_i]);
				d.className += " has-error";
				//add message
				var e = document.getElementById("ctomesserror_"+ctojs_fiel[cto_i]);
				e.innerHTML = ctoajx_response["err_"+ctojs_fiel[cto_i]];
			}
		}
	}
	
	function ctoerr_messages_clear(ctojs_fiel){
		for(cto_i = 0; cto_i < ctojs_fiel.length; cto_i++){
			//remove class has-error
			var d = document.getElementById("ctof_"+ctojs_fiel[cto_i]);
			d.className = d.className.replace(/\bhas-error\b/g, "");
			//remove message
			var e = document.getElementById("ctomesserror_"+ctojs_fiel[cto_i]);
			e.innerHTML = "";
		}
	}
	
